mapper.select:
SELECT product_id,product_name,product_main_img,product_sub_img1,product_sub_img2,product_explain,product_origin_price,product_dc_price,product_company,product_made,product_delivery,product_delivery_fee,stock,num FROM (SELECT product_id,product_name,product_main_img,product_sub_img1,product_sub_img2,product_explain,product_origin_price,product_dc_price,product_company,product_made,product_delivery,product_delivery_fee,stock,rownum as num FROM (SELECT product_id,product_name,product_main_img,product_sub_img1,product_sub_img2,product_explain,product_origin_price,product_dc_price,product_company,product_made,product_delivery,product_delivery_fee,stock FROM acc
WHERE product_name LIKE '%'||#{cate}||'%'
ORDER BY product_id DESC)) WHERE num BETWEEN #{start} AND #{end}
